#Gives a random response 'R' for right answer, 'W' for wrong answer, 'L' for late answer
def get_answ(answtype):
	tup = ()
	if answtype == 'R':
		tup = rightansw
	elif answtype == 'W':
		tup = wrongansw
	elif answtype == 'L':
		tup = lateansw
